   110	// add two 128b data
   111	template <typename T>
   112	inline __device__ int4 add128b(T& a, T& b) {
   113	  T c;
   114	  c.unpacked[0] = a.unpacked[0] + b.unpacked[0];
   115	  c.unpacked[1] = a.unpacked[1] + b.unpacked[1];
   116	  c.unpacked[2] = a.unpacked[2] + b.unpacked[2];
   117	  c.unpacked[3] = a.unpacked[3] + b.unpacked[3];
   118	  return c.packed;
   119	}
