   267	template <typename T, int RANKS_PER_NODE, bool COPY_INPUT = true>
   268	static __global__ void __launch_bounds__(512, 1) twoShotAllReduceKernel(AllReduceParams params) {
   269	  // Suppose that two GPUs participate in the AR exchange, and we start two blocks.
   270	  // The message is partitioned into chunks as detailed below:
   271	  //               message
   272	  //       |-------------------|
   273	  //       |--GPU 0--|--GPU 1--| (GPU responsibility parts)
   274	  // GPU 0 | B0 | B1 | B0 | B1 |
   275	  // GPU 1 | B0 | B1 | B0 | B1 |
   276	  //
   277	  // Here the step-by-step behavior of one block:
   278	  // 1. B0 copies all chunks is it responsible for, from local_input to shareable buffer
   279	  // 2. B0 on GPU 0 and B0 on GPU 1 wait for each other (block_barrier #0)
   280	  // 3. B0 on GPU 0 gather and sum the B0 chunks from GPU 1, that are in the GPU 0 responsibility
   281	  //    part (the first half of the message, see GPU responsibility row above)
   282	  // 3bis. Likewise, B0 on GPU 1 copies and sum the chunks for GPU 0,
   283	  //       where GPU 1 is responsible: the second half of the message.
   284	  // 4. B0 on GPU 0 and B0 on GPU 1 wait for each other (block_barrier #1)
   285	  // 5. B0 writes result to local_output. It gathers each chunk from its responsible GPU.
   286	  //    For example, here it reads the first chunk from GPU 0 and second chunk from GPU 1.
   287	  //
   288	  // With COPY_INPUT == false, skip step 1. and use gpu_barrier instead of block barrier during step 2.
   289	  // We only to know if the other GPU as arrived at the AR kernel, that would mean that data is ready
   290	  // to be read.
   291	  //
   292	  // Note that compared to one-shot, one block (CTA) writes multiple input chunks and write multiple output chunks.
   293	  // However, it's only responsible for the summation of a single chunk.
   294	  //
   295	  // With PUSH_MODE, we consider that the shared buffer is of size:
   296	  // params.peer_comm_buffer_ptrs: [world_size, world_size, message_size / world_size]
   297	  //
   298	  // Here the step-by-step behavior of one block:
   299	  // 1. B0 push the chunks is it responsible for into the corresponding GPUs:
   300	  //    params.peer_comm_buffer_ptrs[target_gpu, local_gpu, current B0 slice]
   301	  // 2. block sync so the blocks have been shared by other GPUs
   302	  // 3. Reduce along second dimension params.peer_comm_buffer_ptrs[local_gpu, :, B0 slice]
   303	  // 4. block barrier (corresponding blocks have finished reduction)
   304	  // 5. pull and write on local buffer, by reading params.peer_comm_buffer_ptrs[:, 0, B0 slice] (reduction result is
   305	  //    written at index 0 of 2nd dim)
   306	
   307	  int const bidx = blockIdx.x;
   308	  int const tidx = threadIdx.x;
   309	  int const grid_size = gridDim.x;
   310	
   311	  // The number of elements packed into one for comms
   312	  static constexpr int PACKED_ELTS = 16 / sizeof(T);
   313	  using PackedType = typename PackedOn16Bytes<T>::Type;
   314	
   315	  T const* local_input_buffer = reinterpret_cast<T const*>(params.local_input_buffer_ptr);
   316	  auto peer_comm_buffer_ptrs = params.peer_comm_buffer_ptrs->ptrs;
   317	  T* local_shared_buffer = reinterpret_cast<T*>(peer_comm_buffer_ptrs[params.local_rank]);
   318	  T* local_output_buffer = reinterpret_cast<T*>(params.local_output_buffer_ptr);
   319	
   320	  size_t const chunk_start = bidx * params.elts_per_block + tidx * PACKED_ELTS;
   321	  size_t const chunk_end = min(chunk_start + params.elts_per_block, params.elts_per_rank);
   322	
   323	  T* buffers[RANKS_PER_NODE];
   324	  T* buffers_unorder[RANKS_PER_NODE];
   325	  int ranks[RANKS_PER_NODE];
   326	#pragma unroll
   327	  for (int ii = 0; ii < RANKS_PER_NODE; ++ii) {
   328	    // A mapping of the ranks to scatter reads as much as possible
   329	    int rank = (params.local_rank + ii) % RANKS_PER_NODE;
   330	    ranks[ii] = rank;
   331	    buffers[ii] = reinterpret_cast<T*>(peer_comm_buffer_ptrs[rank]);
   332	    buffers_unorder[ii] = reinterpret_cast<T*>(peer_comm_buffer_ptrs[ii]);
   333	  }
   334	
   335	#if (defined(__CUDACC_VER_MAJOR__) && (__CUDACC_VER_MAJOR__ >= 12))
   336	#if (defined(__CUDA_ARCH__) && (__CUDA_ARCH__ >= 900))
   337	  cudaGridDependencySynchronize();
   338	#endif
   339	#endif
   340	
   341	  if constexpr (COPY_INPUT) {
   342	    // Copy all blocks from local buffer to shareable buffer
   343	    for (size_t local_offset = chunk_start; local_offset < chunk_end; local_offset += blockDim.x * PACKED_ELTS) {
   344	#pragma unroll
   345	      for (int ii = 0; ii < RANKS_PER_NODE; ++ii) {
   346	        size_t offset_rank = ranks[ii] * params.elts_per_rank + local_offset;
   347	        if (offset_rank >= params.elts_total) {
