   424	std::tuple<int, int> kernelLaunchConfig(AllReduceStrategyType algo, AllReduceParams& params, size_t elts_per_thread) {
   425	  int blocks_per_grid = 1, threads_per_block = DEFAULT_BLOCK_SIZE;
   426	  switch (algo) {
   427	    case AllReduceStrategyType::ONESHOT: {
   428	      assert(params.elts_total % elts_per_thread == 0);
   429	      size_t const total_threads = roundUp(params.elts_total / elts_per_thread, WARP_SIZE);
   430	      threads_per_block = std::min(DEFAULT_BLOCK_SIZE, total_threads);
   431	      blocks_per_grid = std::min(static_cast<int>(MAX_ALL_REDUCE_BLOCKS), divUp(total_threads, threads_per_block));
   432	      params.elts_per_block = roundUp(divUp(params.elts_total, blocks_per_grid), elts_per_thread);
   433	      params.elts_per_rank = params.elts_total;
   434	      break;
   435	    }
   436	    case AllReduceStrategyType::TWOSHOT: {
   437	      assert(params.elts_total % (elts_per_thread * params.ranks_per_node) == 0);
   438	      size_t const total_threads = roundUp(params.elts_total / (elts_per_thread * params.ranks_per_node), WARP_SIZE);
