   189	template <typename T, int RANKS_PER_NODE, bool COPY_INPUT = true>
   190	static __global__ void __launch_bounds__(512, 1) oneShotAllReduceKernel(AllReduceParams params) {
   191	  // Suppose that two GPUs participate in the AR exchange, and we start four blocks.
   192	  // The message is partitioned into chunks as detailed below:
   193	  //               message
   194	  //       |-------------------|
   195	  // GPU 0 | B0 | B1 | B2 | B3 |
   196	  // GPU 1 | B0 | B1 | B2 | B3 |
   197	  //
   198	  // Here the step-by-step behavior of one block:
   199	  // 1. B0 copies the chunk it  is responsible for, from local_input to shareable buffer
   200	  // 2. B0 on GPU 0 and B0 on GPU 1 wait for each other (block_barrier)
   201	  // 3. B0 on GPU 0 pull and sum the chunk from GPU 1, writes the result to local_output
   202	  //
   203	  // With COPY_INPUT == false, skip step 1. and use gpu_barrier instead of block barrier during step 2.
   204	  // We only to know if the other GPU as arrived at the AR kernel, that would mean that data is ready
   205	  //
   206	  // With PUSH_MODE, we consider that the shared buffer is of size:
   207	  // params.peer_comm_buffer_ptrs: [world_size, world_size, message_size]
   208	  //
   209	  // Here the step-by-step behavior of one block:
   210	  // 1. B0 push the chunk is it responsible for into all other GPUs:
   211	  //    params.peer_comm_buffer_ptrs[:, local_gpu, B0 slice]
   212	  // 2. block sync so the block is shared by other GPUs
   213	  // 3. Reduce along second dimension params.peer_comm_buffer_ptrs[local_gpu, :, B0 slice]
   214	
   215	  int const bidx = blockIdx.x;
   216	  int const tidx = threadIdx.x;
   217	  int const grid_size = gridDim.x;
   218	
   219	  // The number of elements packed into one for comms
   220	  static constexpr int NUM_ELTS = 16 / sizeof(T);
   221	
   222	  // Packed data type for comms
   223	  using PackedStruct = typename PackedOn16Bytes<T>::Type;
   224	
   225	  // The source pointers. Distributed round-robin for the different warps.
   226	  auto peer_comm_buffer_ptrs = params.peer_comm_buffer_ptrs->ptrs;
   227	  T* local_shared_buffer = reinterpret_cast<T*>(peer_comm_buffer_ptrs[params.local_rank]);
   228	  // Start and end offsets of the thread
   229	  size_t chunk_start = bidx * params.elts_per_block + tidx * NUM_ELTS;
   230	  size_t chunk_end = std::min((bidx + 1) * params.elts_per_block, params.elts_per_rank);
   231	
   232	  if constexpr (COPY_INPUT) {
   233	    T const* local_input_buffer = reinterpret_cast<T const*>(params.local_input_buffer_ptr);
   234	    // Copy from local buffer to shareable buffer
   235	    for (size_t iter_offset = chunk_start; iter_offset < chunk_end; iter_offset += blockDim.x * NUM_ELTS) {
   236	      *reinterpret_cast<int4*>(&local_shared_buffer[iter_offset]) =
   237	          *reinterpret_cast<int4 const*>(&local_input_buffer[iter_offset]);
   238	    }
   239	  }
   240	  // wait for equivalent blocks of other GPUs to have copied data to their shareable buffer
   241	  block_barrier<true>(
   242	      params.peer_barrier_ptrs_in, params.barrier_flag, params.local_rank, RANKS_PER_NODE, tidx, bidx, grid_size);
   243	
   244	  // Each block accumulates the values from the different GPUs on the same node.
   245	  for (size_t iter_offset = chunk_start; iter_offset < chunk_end; iter_offset += blockDim.x * NUM_ELTS) {
   246	    // Iterate over the different ranks/devices on the node to load the values.
   247	    PackedStruct vals[RANKS_PER_NODE];
   248	#pragma unroll
   249	    for (int ii = 0; ii < RANKS_PER_NODE; ++ii) {
   250	      vals[ii].packed = *reinterpret_cast<int4 const*>(&((T*)peer_comm_buffer_ptrs[ii])[iter_offset]);
   251	    }
   252	
   253	    // Sum the values from the different ranks.
   254	    PackedStruct sums;
   255	    sums.packed = {0, 0, 0, 0};
   256	#pragma unroll
   257	    for (int rank = 0; rank < RANKS_PER_NODE; ++rank) {
   258	      // Always reduce from rank 0 to ensure stable reduce order.
   259	      sums.packed = add128b(sums, vals[rank]);
   260	    }
   261	
   262	    // Store to the destination buffer.
   263	    *reinterpret_cast<int4*>(&reinterpret_cast<T*>(params.local_output_buffer_ptr)[iter_offset]) = sums.packed;
   264	  }
   265	}
