   150	template <bool start, bool need_fence = false>
   151	__inline__ __device__ void block_barrier(
   152	    uint32_t** signals,
   153	    uint32_t const flag,
   154	    size_t const local_rank,
   155	    size_t const world_size,
   156	    int const tidx,
   157	    int const bidx,
   158	    int const grid_size) {
   159	  if constexpr (!start) {
   160	    __syncthreads();
   161	  }
   162	  // After this function, the block of id == bidx of each GPU has reached the barrier
   163	  if (tidx < world_size) {
   164	    // we can think of signals having the shape [world_size, 2, num_blocks, world_size]
   165	    // (+ an offset on dim 2 to account for flags used in multi_gpu_barrier)
   166	    // Dimension 0 is the "listening" dimension, dimension 3 is "emitting" dimension
   167	
   168	    // Block broadcast its flag (local_rank on emitting dimension) to all receivers
   169	    uint32_t flag_block_offset = world_size + bidx * world_size;
   170	
   171	    flag_block_offset += (grid_size + 1) * world_size * (flag % 2);
   172	
   173	    uint32_t* peer_barrier_d = signals[local_rank] + flag_block_offset + tidx;
   174	    // Blocks check that corresponding blocks on other GPUs have also set the flag
   175	    if constexpr (need_fence) {
   176	      st_flag_release(flag, signals[tidx] + flag_block_offset + local_rank);
   177	      while (ld_flag_acquire(peer_barrier_d) != flag) {
   178	      }
   179	    } else {
   180	      st_flag_volatile(flag, signals[tidx] + flag_block_offset + local_rank);
   181	      while (ld_flag_volatile(peer_barrier_d) != flag) {
   182	      }
   183	    }
   184	  }
   185	
   186	  __syncthreads();
   187	}
