   316	  auto peer_comm_buffer_ptrs = params.peer_comm_buffer_ptrs->ptrs;
   317	  T* local_shared_buffer = reinterpret_cast<T*>(peer_comm_buffer_ptrs[params.local_rank]);
   318	  T* local_output_buffer = reinterpret_cast<T*>(params.local_output_buffer_ptr);
   319	
   320	  size_t const chunk_start = bidx * params.elts_per_block + tidx * PACKED_ELTS;
   321	  size_t const chunk_end = min(chunk_start + params.elts_per_block, params.elts_per_rank);
   322	
   323	  T* buffers[RANKS_PER_NODE];
   324	  T* buffers_unorder[RANKS_PER_NODE];
   325	  int ranks[RANKS_PER_NODE];
