   327	  for (int ii = 0; ii < RANKS_PER_NODE; ++ii) {
   328	    // A mapping of the ranks to scatter reads as much as possible
   329	    int rank = (params.local_rank + ii) % RANKS_PER_NODE;
   330	    ranks[ii] = rank;
   331	    buffers[ii] = reinterpret_cast<T*>(peer_comm_buffer_ptrs[rank]);
   332	    buffers_unorder[ii] = reinterpret_cast<T*>(peer_comm_buffer_ptrs[ii]);
   333	  }
