// Optimization: Restructure for better branch prediction and early returns
inline AllReduceStrategyType SelectImplementation(size_t message_size, int world_size) {
  // Fast path: Most common case first
  if (world_size <= 2) {
    return AllReduceStrategyType::ONESHOT;
  }

  const size_t maxWorkspaceSize = GetMaxRequiredWorkspaceSize(world_size);
  if (message_size > maxWorkspaceSize) {
    assert(false && "Custom allreduce do not ring currently");
    return AllReduceStrategyType::RING;
  }

  // Optimization: Use compile-time constants for thresholds
  constexpr size_t ONESHOT_THRESHOLD_SMALL = 1 * 1024 * 1024;
  constexpr size_t ONESHOT_THRESHOLD_LARGE = 512 * 1024;

  if (world_size <= 4) {
    return (message_size < ONESHOT_THRESHOLD_SMALL) ? AllReduceStrategyType::ONESHOT : AllReduceStrategyType::TWOSHOT;
  }

  return (message_size < ONESHOT_THRESHOLD_LARGE) ? AllReduceStrategyType::ONESHOT : AllReduceStrategyType::TWOSHOT;
}

