     6	template <typename scalar_t, int SIZE>
     7	inline void softmax(float* __restrict__ out, const scalar_t* __restrict__ input) {
     8	  using bVec = at::vec::Vectorized<scalar_t>;
     9	  using fVec = at::vec::Vectorized<float>;
    10	
    11	  constexpr int kVecSize = bVec::size();
    12	
    13	  // step 1: get max
    14	  fVec max_fvec = fVec(-std::numeric_limits<float>::infinity());
    15	  if constexpr (SIZE < kVecSize) {
    16	    // SIZE = 1, 2, 4, 8, 16; only the top half is used
    17	    bVec x_bvec = bVec::loadu(input, SIZE);
    18	    fVec x_fvec0, x_fvec1;
    19	    std::tie(x_fvec0, x_fvec1) = at::vec::convert_to_float(x_bvec);
    20	    x_fvec0 = fVec::set(max_fvec, x_fvec0, SIZE);
    21	    max_fvec = at::vec::maximum(max_fvec, x_fvec0);
    22	    x_fvec0.store(out, SIZE);
    23	  } else {
    24	    for (int d = 0; d < SIZE; d += kVecSize) {
    25	      bVec x_bvec = bVec::loadu(input + d);
    26	      fVec x_fvec0, x_fvec1;
    27	      std::tie(x_fvec0, x_fvec1) = at::vec::convert_to_float(x_bvec);
    28	
    29	      max_fvec = at::vec::maximum(max_fvec, x_fvec0);
    30	      max_fvec = at::vec::maximum(max_fvec, x_fvec1);
    31	      x_fvec0.store(out + d);
    32	      x_fvec1.store(out + d + fVec::size());
    33	    }
    34	  }
    35	  float max_val = vec_reduce_max(max_fvec);
    36	  max_fvec = fVec(max_val);
    37	
    38	  // step 2: sum of (x - max).exp()
    39	  fVec sum_fvec = fVec(float(0));
    40	  if constexpr (SIZE < fVec::size()) {
    41	    // SIZE = 1, 2, 4, 8
    42	    fVec x_fvec = (fVec::loadu(out, SIZE) - max_fvec).exp_u20();
    43	    x_fvec = fVec::set(sum_fvec, x_fvec, SIZE);
    44	    sum_fvec += x_fvec;
    45	    x_fvec.store(out, SIZE);
    46	  } else {
    47	    for (int d = 0; d < SIZE; d += fVec::size()) {
    48	      fVec x_fvec = (fVec::loadu(out + d) - max_fvec).exp_u20();
    49	      sum_fvec += x_fvec;
    50	      x_fvec.store(out + d);
    51	    }
    52	  }
    53	  float sum_val = vec_reduce_sum(sum_fvec);
    54	
    55	  // step 3: x * (1 / sum)
    56	  sum_fvec = fVec(1.f / sum_val);
    57	  if constexpr (SIZE < fVec::size()) {
    58	    // SIZE = 1, 2, 4, 8
    59	    fVec out_fvec = fVec::loadu(out, SIZE) * sum_fvec;
    60	    out_fvec.store(out, SIZE);
    61	  } else {
    62	    for (int d = 0; d < SIZE; d += fVec::size()) {
    63	      fVec out_fvec = fVec::loadu(out + d) * sum_fvec;
    64	      out_fvec.store(out + d);
    65	    }
    66	  }
