    77	    int64_t topk_group,
    78	    bool renormalize) {
    79	  const int64_t num_experts_per_group = NUM_EXPERTS / num_groups;
    80	  at::parallel_for(0, num_tokens, 0, [&](int64_t begin, int64_t end) {
    81	    alignas(64) float scores[NUM_EXPERTS];
