    86	
    87	    for (int64_t i = begin; i < end; ++i) {
    88	      // do softmax to get scores
    89	      softmax<scalar_t, NUM_EXPERTS>(scores, gating_output + i * NUM_EXPERTS);
    90	
    91	      // find max score per group
    92	      for (int64_t g = 0; g < num_groups; ++g) {
    93	        float gmax = -std::numeric_limits<float>::infinity();
