   139	template <typename scalar_t, int SIZE>
   140	inline void sigmoid(float* __restrict__ out, const scalar_t* __restrict__ input) {
   141	  using bVec = at::vec::Vectorized<scalar_t>;
   142	  using fVec = at::vec::Vectorized<float>;
   143	
   144	  const fVec one = fVec(1.f);
   145	
   146	  constexpr int kVecSize = bVec::size();
   147	  for (int d = 0; d < SIZE; d += kVecSize) {
   148	    bVec x_bvec = bVec::loadu(input + d);
   149	    fVec x_fvec0, x_fvec1;
   150	    std::tie(x_fvec0, x_fvec1) = at::vec::convert_to_float(x_bvec);
   151	
   152	    x_fvec0 = one / (one + x_fvec0.neg().exp_u20());
   153	    x_fvec1 = one / (one + x_fvec1.neg().exp_u20());
   154	
   155	    x_fvec0.store(out + d);
   156	    x_fvec1.store(out + d + fVec::size());
   157	  }
   158	}
