   160	template <typename scalar_t, int SIZE>
   161	inline void
   162	apply_bias(float* __restrict__ scores2, const float* __restrict__ scores, const scalar_t* __restrict__ bias) {
   163	  using bVec = at::vec::Vectorized<scalar_t>;
   164	  using fVec = at::vec::Vectorized<float>;
   165	  for (int d = 0; d < SIZE; d += bVec::size()) {
   166	    bVec bias_vec = bVec::loadu(bias + d);
   167	    fVec bias0, bias1;
   168	    std::tie(bias0, bias1) = at::vec::convert_to_float(bias_vec);
   169	
   170	    fVec x0 = fVec::loadu(scores + d) + bias0;
   171	    fVec x1 = fVec::loadu(scores + d + fVec::size()) + bias1;
   172	    x0.store(scores2 + d);
   173	    x1.store(scores2 + d + fVec::size());
   174	  }
   175	}
