   177	template <typename scalar_t, int NUM_EXPERTS, int TOPK>
   178	void biased_grouped_topk_kernel_impl(
   179	    float* __restrict__ topk_weights,
   180	    int32_t* __restrict__ topk_ids,
   181	    const scalar_t* __restrict__ gating_output,
   182	    const scalar_t* __restrict__ bias,
   183	    int64_t num_tokens,
   184	    int64_t num_groups,
   185	    int64_t topk_group,
   186	    bool renormalize) {
   187	  using Vec = at::vec::Vectorized<float>;
   188	
   189	  const int64_t num_experts_per_group = NUM_EXPERTS / num_groups;
   190	  at::parallel_for(0, num_tokens, 0, [&](int64_t begin, int64_t end) {
   191	    // scores: sigmoid
   192	    alignas(64) float scores[NUM_EXPERTS];
   193	    // scores for choice: sigmoid + bias
   194	    alignas(64) float scores2[NUM_EXPERTS];
   195	
   196	    using elem_t = std::pair<float, int32_t>;
   197	    std::vector<elem_t> queue(num_groups);
   198	    std::vector<elem_t> queue2(topk_group * num_experts_per_group);
   199	
   200	    for (int64_t i = begin; i < end; ++i) {
   201	      // do sigmoid to get scores
   202	      sigmoid<scalar_t, NUM_EXPERTS>(scores, gating_output + i * NUM_EXPERTS);
   203	      apply_bias<scalar_t, NUM_EXPERTS>(scores2, scores, bias);
   204	
   205	      for (int64_t g = 0; g < num_groups; ++g) {
   206	        // find the max
   207	        float gmax = at::vec::reduce_all<float>(
   208	            [](Vec& x, Vec& y) { return at::vec::maximum(x, y); },
   209	            scores2 + g * num_experts_per_group,
   210	            num_experts_per_group);
   211	
   212	        // find position of first max,
   213	        // note that we may have multiple max values.
   214	        int first_max_idx = -1;
   215	        for (int64_t e = 0; e < num_experts_per_group; ++e) {
   216	          if (scores2[g * num_experts_per_group + e] == gmax) {
   217	            first_max_idx = g * num_experts_per_group + e;
   218	            break;
   219	          }
   220	        }
   221	
   222	        // find the 2nd max
   223	        scores2[first_max_idx] = -std::numeric_limits<float>::infinity();
   224	        float gmax2 = at::vec::reduce_all<float>(
   225	            [](Vec& x, Vec& y) { return at::vec::maximum(x, y); },
   226	            scores2 + g * num_experts_per_group,
   227	            num_experts_per_group);
   228	        // restore scores for choice
   229	        scores2[first_max_idx] = gmax;
   230	
   231	        queue[g] = {gmax + gmax2, g};
   232	      }
   233	
   234	      // find group topk
   235	      std::partial_sort(
   236	          queue.begin(), queue.begin() + topk_group, queue.end(), [](const elem_t& x, const elem_t& y) -> bool {
   237	            return x.first > y.first;
   238	          });
   239	
   240	      for (int64_t g = 0; g < topk_group; ++g) {
   241	        int32_t group_idx = queue[g].second;
   242	        for (int64_t e = 0; e < num_experts_per_group; ++e) {
   243	          int32_t expert_idx = group_idx * num_experts_per_group + e;
   244	          queue2[g * num_experts_per_group + e] = {scores2[expert_idx], expert_idx};
   245	        }
   246	      }
   247	
   248	      // find global topk
   249	      std::partial_sort(
   250	          queue2.begin(), queue2.begin() + TOPK, queue2.end(), [](const elem_t& x, const elem_t& y) -> bool {
   251	            return x.first > y.first;
   252	          });
   253	
   254	      for (int j = 0; j < TOPK; ++j) {
   255	        int32_t index = queue2[j].second;
   256	        topk_ids[i * TOPK + j] = index;
   257	        topk_weights[i * TOPK + j] = scores[index];
   258	      }
   259	
   260	#if defined(CPU_CAPABILITY_AVX512)
   261	      if (renormalize) {
   262	        __mmask16 mask = (1ULL << TOPK) - 1;
   263	        __m512 x = _mm512_maskz_loadu_ps(mask, topk_weights + i * TOPK);
   264	        float sum = _mm512_reduce_add_ps(x);
   265	        __m512 vscale = _mm512_set1_ps(1.f / sum);
   266	        __m512 y = _mm512_mul_ps(x, vscale);
   267	        _mm512_mask_storeu_ps(topk_weights + i * TOPK, mask, y);
   268	      }
   269	#else
   270	      if (renormalize) {
   271	        float sum = 0.f;
   272	        for (int64_t j = 0; j < TOPK; ++j) {
   273	          sum += topk_weights[i * TOPK + j];
   274	        }
   275	        float scale = 1.f / sum;
   276	        for (int64_t j = 0; j < TOPK; ++j) {
   277	          topk_weights[i * TOPK + j] *= scale;
   278	        }
   279	      }
   280	#endif
   281	    }
   282	  });
   283	}
