   317	  RECORD_FUNCTION("sgl-kernel::grouped_topk_cpu", std::vector<c10::IValue>({hidden_states, gating_output}));
   318	  CHECK_INPUT(gating_output);
   319	
   320	  const auto st = hidden_states.scalar_type();
   321	  CHECK_EQ(gating_output.scalar_type(), st);
