   328	
   329	  AT_DISPATCH_REDUCED_FLOATING_TYPES(st, "grouped_topk_kernel", [&] {
   330	    switch (num_experts) {
   331	      case 1:
   332	        LAUNCH_GROUPED_TOPK_KERNEL(1);
   333	        break;
   334	      case 2:
   335	        LAUNCH_GROUPED_TOPK_KERNEL(2);
   336	        break;
   337	      case 4:
   338	        LAUNCH_GROUPED_TOPK_KERNEL(4);
   339	        break;
   340	      case 8:
   341	        LAUNCH_GROUPED_TOPK_KERNEL(8);
   342	        break;
   343	      case 16:
   344	        LAUNCH_GROUPED_TOPK_KERNEL(16);
   345	        break;
   346	      case 32:
   347	        LAUNCH_GROUPED_TOPK_KERNEL(32);
   348	        break;
   349	      case 64:
   350	        LAUNCH_GROUPED_TOPK_KERNEL(64);
   351	        break;
   352	      case 128:
   353	        LAUNCH_GROUPED_TOPK_KERNEL(128);
   354	        break;
   355	      case 160:
   356	        LAUNCH_GROUPED_TOPK_KERNEL(160);
