#!/usr/bin/env python3
"""
Performance test for memory pool optimizations
"""
import time
import torch
import sys
import os

# Add the python directory to the path
sys.path.insert(0, os.path.join(os.path.dirname(__file__), '..', 'python'))

def test_memory_pool_performance():
    """Test memory pool allocation performance"""
    device = "cuda" if torch.cuda.is_available() else "cpu"
    print(f"Testing on device: {device}")

    # Test parameters
    size = 10000
    max_context_len = 4096
    head_num = 32
    head_dim = 128
    layer_num = 32
    heavy_channel_num = 64

    # Warm up
    if device == "cuda":
        torch.cuda.synchronize()

    # Test 1: ReqToTokenPool allocation
    print("\n=== Test 1: ReqToTokenPool allocation ===")
    start = time.perf_counter()
    for _ in range(10):
        req_to_token = torch.zeros(
            (size, max_context_len), dtype=torch.int32, device=device
        )
        if device == "cuda":
            torch.cuda.synchronize()
    end = time.perf_counter()
    print(f"torch.zeros allocation time (10 iterations): {(end - start)*1000:.2f} ms")

    start = time.perf_counter()
    for _ in range(10):
        req_to_token = torch.empty(
            (size, max_context_len), dtype=torch.int32, device=device
        )
        if device == "cuda":
            torch.cuda.synchronize()
    end = time.perf_counter()
    print(f"torch.empty allocation time (10 iterations): {(end - start)*1000:.2f} ms")

    # Test 2: Label buffer allocation
    print("\n=== Test 2: Label buffer allocation ===")
    start = time.perf_counter()
    for _ in range(5):
        label_buffer = [
            torch.zeros(
                (size + 1, head_num, heavy_channel_num), dtype=torch.float32, device=device
            )
            for _ in range(layer_num)
        ]
        if device == "cuda":
            torch.cuda.synchronize()
    end = time.perf_counter()
    print(f"torch.zeros label buffer time (5 iterations): {(end - start)*1000:.2f} ms")

    start = time.perf_counter()
    for _ in range(5):
        label_buffer = [
            torch.empty(
                (size + 1, head_num, heavy_channel_num), dtype=torch.float32, device=device
            )
            for _ in range(layer_num)
        ]
        if device == "cuda":
            torch.cuda.synchronize()
    end = time.perf_counter()
    print(f"torch.empty label buffer time (5 iterations): {(end - start)*1000:.2f} ms")

    # Test 3: Custom memory pool context overhead
    print("\n=== Test 3: Memory pool context overhead ===")
    if device == "cuda":
        # Test without custom memory pool
        start = time.perf_counter()
        for _ in range(10):
            buf = torch.empty((1024, 1024), dtype=torch.float32, device=device)
            torch.cuda.synchronize()
        end = time.perf_counter()
        print(f"Standard allocation time (10 iterations): {(end - start)*1000:.2f} ms")

    print("\n=== Performance test completed ===")

if __name__ == "__main__":
    test_memory_pool_performance()

