   191	template <typename T, int RANKS_PER_NODE, bool COPY_INPUT = true>
   192	static __global__ void __launch_bounds__(512, 1) oneShotAllReduceKernel(AllReduceParams params) {
   193	  // Suppose that two GPUs participate in the AR exchange, and we start four blocks.
   194	  // The message is partitioned into chunks as detailed below:
   195	  //               message
   196	  //       |-------------------|
   197	  // GPU 0 | B0 | B1 | B2 | B3 |
   198	  // GPU 1 | B0 | B1 | B2 | B3 |
   199	  //
   200	  // Here the step-by-step behavior of one block:
   201	  // 1. B0 copies the chunk it  is responsible for, from local_input to shareable buffer
   202	  // 2. B0 on GPU 0 and B0 on GPU 1 wait for each other (block_barrier)
   203	  // 3. B0 on GPU 0 pull and sum the chunk from GPU 1, writes the result to local_output
   204	  //
   205	  // With COPY_INPUT == false, skip step 1. and use gpu_barrier instead of block barrier during step 2.
   206	  // We only to know if the other GPU as arrived at the AR kernel, that would mean that data is ready
   207	  //
   208	  // With PUSH_MODE, we consider that the shared buffer is of size:
   209	  // params.peer_comm_buffer_ptrs: [world_size, world_size, message_size]
   210	  //
   211	  // Here the step-by-step behavior of one block:
   212	  // 1. B0 push the chunk is it responsible for into all other GPUs:
   213	  //    params.peer_comm_buffer_ptrs[:, local_gpu, B0 slice]
   214	  // 2. block sync so the block is shared by other GPUs
   215	  // 3. Reduce along second dimension params.peer_comm_buffer_ptrs[local_gpu, :, B0 slice]
   216	
   217	  int const bidx = blockIdx.x;
   218	  int const tidx = threadIdx.x;
   219	  int const grid_size = gridDim.x;
   220	
   221	  // The number of elements packed into one for comms
   222	  static constexpr int NUM_ELTS = 16 / sizeof(T);
   223	
   224	  // Packed data type for comms
   225	  using PackedStruct = typename PackedOn16Bytes<T>::Type;
   226	
   227	  // The source pointers. Distributed round-robin for the different warps.
   228	  auto peer_comm_buffer_ptrs = params.peer_comm_buffer_ptrs->ptrs;
   229	  T* __restrict__ local_shared_buffer = reinterpret_cast<T*>(peer_comm_buffer_ptrs[params.local_rank]);
   230	  // Start and end offsets of the thread
   231	  size_t chunk_start = bidx * params.elts_per_block + tidx * NUM_ELTS;
   232	  size_t chunk_end = min((bidx + 1) * params.elts_per_block, params.elts_per_rank);
   233	  T* __restrict__ local_output_buffer = reinterpret_cast<T*>(params.local_output_buffer_ptr);
   234	
   235	  if constexpr (COPY_INPUT) {
   236	    T const* __restrict__ local_input_buffer = reinterpret_cast<T const*>(params.local_input_buffer_ptr);
   237	    // Copy from local buffer to shareable buffer
   238	    for (size_t iter_offset = chunk_start; iter_offset < chunk_end; iter_offset += blockDim.x * NUM_ELTS) {
   239	      *reinterpret_cast<int4*>(&local_shared_buffer[iter_offset]) =
   240	          *reinterpret_cast<int4 const*>(&local_input_buffer[iter_offset]);
   241	    }
   242	  }
   243	  // wait for equivalent blocks of other GPUs to have copied data to their shareable buffer
   244	  block_barrier<true>(
   245	      params.peer_barrier_ptrs_in, params.barrier_flag, params.local_rank, RANKS_PER_NODE, tidx, bidx, grid_size);
   246	
   247	  // Each block accumulates the values from the different GPUs on the same node.
   248	  for (size_t iter_offset = chunk_start; iter_offset < chunk_end; iter_offset += blockDim.x * NUM_ELTS) {
   249	    // Sum the values from the different ranks in a fixed order to ensure determinism.
   250	    PackedStruct sums;
   251	    sums.packed = make_int4(0, 0, 0, 0);
   252	#pragma unroll
   253	    for (int rank = 0; rank < RANKS_PER_NODE; ++rank) {
   254	      // Always reduce from rank 0 to ensure stable reduce order.
   255	      PackedStruct val;
   256	      val.packed = *reinterpret_cast<int4 const*>(&((T*)peer_comm_buffer_ptrs[rank])[iter_offset]);
   257	      sums.packed = add128b(sums, val);
   258	    }
   259	
   260	    // Store to the destination buffer.
   261	    *reinterpret_cast<int4*>(&local_output_buffer[iter_offset]) = sums.packed;
   262	  }
   263	  // Signal completion for corresponding blocks on other GPUs. No fence needed here.
   264	  block_barrier<false>(
   265	      params.peer_barrier_ptrs_out, params.barrier_flag, params.local_rank, RANKS_PER_NODE, tidx, bidx, grid_size);
   266	}
