   268	template <typename T, int RANKS_PER_NODE, bool COPY_INPUT = true>
   269	static __global__ void __launch_bounds__(512, 1) twoShotAllReduceKernel(AllReduceParams params) {
   270	  // Suppose that two GPUs participate in the AR exchange, and we start two blocks.
   271	  // The message is partitioned into chunks as detailed below:
   272	  //               message
   273	  //       |-------------------|
   274	  //       |--GPU 0--|--GPU 1--| (GPU responsibility parts)
   275	  // GPU 0 | B0 | B1 | B0 | B1 |
   276	  // GPU 1 | B0 | B1 | B0 | B1 |
   277	  //
   278	  // Here the step-by-step behavior of one block:
   279	  // 1. B0 copies all chunks is it responsible for, from local_input to shareable buffer
   280	  // 2. B0 on GPU 0 and B0 on GPU 1 wait for each other (block_barrier #0)
   281	  // 3. B0 on GPU 0 gather and sum the B0 chunks from GPU 1, that are in the GPU 0 responsibility
   282	  //    part (the first half of the message, see GPU responsibility row above)
   283	  // 3bis. Likewise, B0 on GPU 1 copies and sum the chunks for GPU 0,
   284	  //       where GPU 1 is responsible: the second half of the message.
   285	  // 4. B0 on GPU 0 and B0 on GPU 1 wait for each other (block_barrier #1)
   286	  // 5. B0 writes result to local_output. It gathers each chunk from its responsible GPU.
   287	  //    For example, here it reads the first chunk from GPU 0 and second chunk from GPU 1.
   288	  //
   289	  // With COPY_INPUT == false, skip step 1. and use gpu_barrier instead of block barrier during step 2.
   290	  // We only to know if the other GPU as arrived at the AR kernel, that would mean that data is ready
   291	  // to be read.
   292	  //
   293	  // Note that compared to one-shot, one block (CTA) writes multiple input chunks and write multiple output chunks.
   294	  // However, it's only responsible for the summation of a single chunk.
   295	  //
   296	  // With PUSH_MODE, we consider that the shared buffer is of size:
   297	  // params.peer_comm_buffer_ptrs: [world_size, world_size, message_size / world_size]
   298	  //
   299	  // Here the step-by-step behavior of one block:
   300	  // 1. B0 push the chunks is it responsible for into the corresponding GPUs:
   301	  //    params.peer_comm_buffer_ptrs[target_gpu, local_gpu, current B0 slice]
   302	  // 2. block sync so the blocks have been shared by other GPUs
   303	  // 3. Reduce along second dimension params.peer_comm_buffer_ptrs[local_gpu, :, B0 slice]
   304	  // 4. block barrier (corresponding blocks have finished reduction)
   305	  // 5. pull and write on local buffer, by reading params.peer_comm_buffer_ptrs[:, 0, B0 slice] (reduction result is
   306	  //    written at index 0 of 2nd dim)
   307	
   308	  int const bidx = blockIdx.x;
   309	  int const tidx = threadIdx.x;
   310	  int const grid_size = gridDim.x;
   311	
   312	  // The number of elements packed into one for comms
   313	  static constexpr int PACKED_ELTS = 16 / sizeof(T);
   314	  using PackedType = typename PackedOn16Bytes<T>::Type;
   315	
   316	  T const* local_input_buffer = reinterpret_cast<T const*>(params.local_input_buffer_ptr);
   317	  auto peer_comm_buffer_ptrs = params.peer_comm_buffer_ptrs->ptrs;
   318	  T* local_shared_buffer = reinterpret_cast<T*>(peer_comm_buffer_ptrs[params.local_rank]);
   319	  T* local_output_buffer = reinterpret_cast<T*>(params.local_output_buffer_ptr);
   320	
   321	  size_t const chunk_start = bidx * params.elts_per_block + tidx * PACKED_ELTS;
   322	  size_t const chunk_end = min(chunk_start + params.elts_per_block, params.elts_per_rank);
   323	
   324	  T* buffers[RANKS_PER_NODE];
   325	  T* buffers_unorder[RANKS_PER_NODE];
   326	  int ranks[RANKS_PER_NODE];
   327	#pragma unroll
   328	  for (int ii = 0; ii < RANKS_PER_NODE; ++ii) {
   329	    // A mapping of the ranks to scatter reads as much as possible
   330	    int rank = (params.local_rank + ii) % RANKS_PER_NODE;
   331	    ranks[ii] = rank;
   332	    buffers[ii] = reinterpret_cast<T*>(peer_comm_buffer_ptrs[rank]);
   333	    buffers_unorder[ii] = reinterpret_cast<T*>(peer_comm_buffer_ptrs[ii]);
   334	  }
   335	
   336	#if (defined(__CUDACC_VER_MAJOR__) && (__CUDACC_VER_MAJOR__ >= 12))
   337	#if (defined(__CUDA_ARCH__) && (__CUDA_ARCH__ >= 900))
   338	  cudaGridDependencySynchronize();
   339	#endif
   340	#endif
   341	
   342	  if constexpr (COPY_INPUT) {
   343	    // Copy all blocks from local buffer to shareable buffer
   344	    for (size_t local_offset = chunk_start; local_offset < chunk_end; local_offset += blockDim.x * PACKED_ELTS) {
   345	#pragma unroll
   346	      for (int ii = 0; ii < RANKS_PER_NODE; ++ii) {
   347	        size_t offset_rank = ranks[ii] * params.elts_per_rank + local_offset;
   348	        if (offset_rank >= params.elts_total) {
   349	          continue;
   350	        }
   351	        *reinterpret_cast<int4*>(&local_shared_buffer[offset_rank]) =
   352	            *reinterpret_cast<int4 const*>(&local_input_buffer[offset_rank]);
   353	      }
   354	    }
   355	  }
   356	  block_barrier<true>(
   357	      params.peer_barrier_ptrs_in, params.barrier_flag, params.local_rank, RANKS_PER_NODE, tidx, bidx, grid_size);
   358	
   359	  // Each block accumulates the values from the different GPUs on the same node.
   360	  for (size_t local_offset = chunk_start; local_offset < chunk_end; local_offset += blockDim.x * PACKED_ELTS) {
   361	    size_t const responsible_block_offset = local_offset + params.rank_offset;
   362	
   363	    // Iterate over the different ranks/devices on the node to load the values.
   364	    PackedType vals[RANKS_PER_NODE];
   365	#pragma unroll
   366	    for (int ii = 0; ii < RANKS_PER_NODE; ++ii) {
   367	      vals[ii].packed = *reinterpret_cast<int4 const*>(&buffers_unorder[ii][responsible_block_offset]);
   368	    }
   369	
   370	    // Sum the values from the different ranks.
   371	    PackedType sums;
   372	    sums.packed = {0, 0, 0, 0};
   373	#pragma unroll
   374	    for (int rank = 0; rank < RANKS_PER_NODE; ++rank) {
   375	      // Always reduce from rank 0 to ensure stable reduce order.
   376	      sums.packed = add128b(sums, vals[rank]);
   377	    }
   378	
   379	    // Store to the local buffer or tmp buffer
   380	    if constexpr (COPY_INPUT) {
   381	      *reinterpret_cast<int4*>(&local_shared_buffer[responsible_block_offset]) = sums.packed;
   382	    } else {
   383	      *reinterpret_cast<int4*>(&params.tmp_result_buffers[params.local_rank][responsible_block_offset]) = sums.packed;
   384	    }
   385	  }
   386	
   387	  block_barrier<false, true>(
   388	      params.peer_barrier_ptrs_out, params.barrier_flag, params.local_rank, RANKS_PER_NODE, tidx, bidx, grid_size);
