    81	inline AllReduceStrategyType SelectImplementation(size_t message_size, int world_size) {
    82	  const size_t maxWorkspaceSize = GetMaxRequiredWorkspaceSize(world_size);
    83	
    84	  if (message_size > maxWorkspaceSize) {
    85	    assert(false && "Custom allreduce do not ring currently");
    86	    return AllReduceStrategyType::RING;
    87	  }
