   342	  if constexpr (COPY_INPUT) {
   343	    // Copy all blocks from local buffer to shareable buffer
   344	    for (size_t local_offset = chunk_start; local_offset < chunk_end; local_offset += blockDim.x * PACKED_ELTS) {
   345	#pragma unroll
   346	      for (int ii = 0; ii < RANKS_PER_NODE; ++ii) {
   347	        size_t offset_rank = ranks[ii] * params.elts_per_rank + local_offset;
