   331	    ranks[ii] = rank;
   332	    buffers[ii] = reinterpret_cast<T*>(peer_comm_buffer_ptrs[rank]);
   333	    buffers_unorder[ii] = reinterpret_cast<T*>(peer_comm_buffer_ptrs[ii]);
   334	  }
