#!/usr/bin/env python3
"""
Test script to benchmark the DeepSeek V2 attention forward method selection.
This simulates the hot path where sum() is called multiple times on extend_prefix_lens_cpu.
"""

import time
import torch
from dataclasses import dataclass
from enum import IntEnum, auto

# Simulate the ForwardMode class
class ForwardMode:
    def __init__(self, is_extend=False, is_target_verify=False, is_draft_extend=False):
        self._is_extend = is_extend
        self._is_target_verify = is_target_verify
        self._is_draft_extend = is_draft_extend

    def is_extend(self):
        return self._is_extend

    def is_target_verify(self):
        return self._is_target_verify

    def is_draft_extend(self):
        return self._is_draft_extend

# Simulate the ForwardBatch class
@dataclass
class ForwardBatch:
    forward_mode: ForwardMode
    extend_prefix_lens_cpu: list

class AttnForwardMethod(IntEnum):
    MHA = auto()
    MLA = auto()
    MHA_CHUNKED_KV = auto()

# Original implementation (inefficient)
def select_forward_method_original(forward_batch, attention_backend="fa3", 
                                   disable_chunked_prefix_cache=False,
                                   chunked_prefix_cache_threshold=8192):
    if attention_backend == "flashinfer":
        if (
            forward_batch.forward_mode.is_extend()
            and not forward_batch.forward_mode.is_target_verify()
            and not forward_batch.forward_mode.is_draft_extend()
            and sum(forward_batch.extend_prefix_lens_cpu) == 0
        ):
            return AttnForwardMethod.MHA
        else:
            return AttnForwardMethod.MLA
    elif attention_backend == "fa3":
        if (
            forward_batch.forward_mode.is_extend()
            and not disable_chunked_prefix_cache
            and not forward_batch.forward_mode.is_target_verify()
            and not forward_batch.forward_mode.is_draft_extend()
            and sum(forward_batch.extend_prefix_lens_cpu) >= chunked_prefix_cache_threshold
        ):
            return AttnForwardMethod.MHA_CHUNKED_KV
        else:
            return AttnForwardMethod.MLA
    else:
        if (
            forward_batch.forward_mode.is_extend()
            and not forward_batch.forward_mode.is_target_verify()
            and not forward_batch.forward_mode.is_draft_extend()
            and sum(forward_batch.extend_prefix_lens_cpu) == 0
        ):
            return AttnForwardMethod.MHA
        else:
            return AttnForwardMethod.MLA

# Optimized implementation (efficient)
def select_forward_method_optimized(forward_batch, attention_backend="fa3",
                                    disable_chunked_prefix_cache=False,
                                    chunked_prefix_cache_threshold=8192):
    # Precompute frequently used checks to reduce Python-side overhead in hot path.
    mode = forward_batch.forward_mode
    is_extend = mode.is_extend()
    is_target_verify = mode.is_target_verify()
    is_draft_extend = mode.is_draft_extend()
    if forward_batch.extend_prefix_lens_cpu is not None:
        sum_extend_prefix_lens = sum(forward_batch.extend_prefix_lens_cpu)
    else:
        sum_extend_prefix_lens = 0

    if attention_backend == "flashinfer":
        if (
            is_extend
            and not is_target_verify
            and not is_draft_extend
            and sum_extend_prefix_lens == 0
        ):
            return AttnForwardMethod.MHA
        else:
            return AttnForwardMethod.MLA
    elif attention_backend == "fa3":
        if (
            is_extend
            and not disable_chunked_prefix_cache
            and not is_target_verify
            and not is_draft_extend
            and (sum_extend_prefix_lens >= chunked_prefix_cache_threshold
                 or sum_extend_prefix_lens == 0)
        ):
            return AttnForwardMethod.MHA_CHUNKED_KV
        else:
            return AttnForwardMethod.MLA
    else:
        if (
            is_extend
            and not is_target_verify
            and not is_draft_extend
            and sum_extend_prefix_lens == 0
        ):
            return AttnForwardMethod.MHA
        else:
            return AttnForwardMethod.MLA

def benchmark():
    # Create test data with various scenarios
    test_cases = [
        # (forward_mode, extend_prefix_lens_cpu, backend)
        (ForwardMode(is_extend=True), [0] * 100, "fa3"),
        (ForwardMode(is_extend=True), [1] * 100, "fa3"),
        (ForwardMode(is_extend=True), list(range(100)), "fa3"),
        (ForwardMode(is_extend=True), [0] * 1000, "flashinfer"),
        (ForwardMode(is_extend=True), [1] * 1000, "flashinfer"),
        (ForwardMode(is_extend=False), [0] * 100, "fa3"),
        (ForwardMode(is_extend=True, is_target_verify=True), [0] * 100, "fa3"),
    ]

    num_iterations = 100000

    print("Benchmarking original implementation...")
    start = time.perf_counter()
    for _ in range(num_iterations):
        for mode, lens, backend in test_cases:
            batch = ForwardBatch(forward_mode=mode, extend_prefix_lens_cpu=lens)
            result = select_forward_method_original(batch, attention_backend=backend)
    original_time = time.perf_counter() - start
    print(f"Original time: {original_time:.4f} seconds")

    print("\nBenchmarking optimized implementation...")
    start = time.perf_counter()
    for _ in range(num_iterations):
        for mode, lens, backend in test_cases:
            batch = ForwardBatch(forward_mode=mode, extend_prefix_lens_cpu=lens)
            result = select_forward_method_optimized(batch, attention_backend=backend)
    optimized_time = time.perf_counter() - start
    print(f"Optimized time: {optimized_time:.4f} seconds")

    speedup = original_time / optimized_time
    print(f"\nSpeedup: {speedup:.2f}x")
    print(f"Time saved: {(original_time - optimized_time):.4f} seconds")

    # Verify correctness
    print("\nVerifying correctness...")
    all_correct = True
    for mode, lens, backend in test_cases:
        batch = ForwardBatch(forward_mode=mode, extend_prefix_lens_cpu=lens)
        result_orig = select_forward_method_original(batch, attention_backend=backend)
        result_opt = select_forward_method_optimized(batch, attention_backend=backend)
        if result_orig != result_opt:
            print(f"MISMATCH: {result_orig} != {result_opt}")
            all_correct = False

    if all_correct:
        print("✓ All results match!")
    else:
        print("✗ Some results don't match!")

if __name__ == "__main__":
    benchmark()

