#!/usr/bin/env python3
"""
Comprehensive benchmark to test the warmup optimization for DP > 1.
This simulates the actual warmup process with timing.
"""
import time
import sys
import os

# Add the python directory to the path
sys.path.insert(0, os.path.join(os.path.dirname(__file__), '..', 'python'))

def simulate_old_warmup(dp_size, skip_tokenizer_init=False):
    """Simulate the OLD warmup logic with a loop."""
    start_time = time.perf_counter()

    # Prepare single request data
    json_data = {
        "sampling_params": {
            "temperature": 0,
            "max_new_tokens": 8,
        },
    }

    if skip_tokenizer_init:
        json_data["input_ids"] = [10, 11, 12]
    else:
        json_data["text"] = "The capital city of France is"

    # Simulate sending dp_size requests sequentially
    for i in range(dp_size):
        # Simulate request overhead
        time.sleep(0.0001)  # 0.1ms per request

    elapsed = time.perf_counter() - start_time
    return elapsed, dp_size

def simulate_new_warmup(dp_size, skip_tokenizer_init=False):
    """Simulate the NEW warmup logic with batched request."""
    start_time = time.perf_counter()

    # Prepare batched request data
    json_data = {
        "sampling_params": {
            "temperature": 0,
            "max_new_tokens": 8,
        },
    }

    if skip_tokenizer_init:
        if dp_size > 1:
            json_data["input_ids"] = [[10, 11, 12] for _ in range(dp_size)]
        else:
            json_data["input_ids"] = [10, 11, 12]
    else:
        if dp_size > 1:
            json_data["text"] = ["The capital city of France is"] * dp_size
        else:
            json_data["text"] = "The capital city of France is"

    # Simulate sending a single batched request
    time.sleep(0.0001)  # 0.1ms for single request

    elapsed = time.perf_counter() - start_time
    return elapsed, 1

def main():
    print("=" * 80)
    print("Warmup Performance Comparison: OLD vs NEW")
    print("=" * 80)

    # Test different DP sizes
    test_cases = [1, 2, 4, 8, 16, 32]

    print("\nOLD Implementation (Sequential Requests):")
    print("-" * 80)
    for dp_size in test_cases:
        elapsed, num_requests = simulate_old_warmup(dp_size)
        print(f"DP Size: {dp_size:3d} | Time: {elapsed*1000:7.3f} ms | Requests: {num_requests:3d}")

    print("\nNEW Implementation (Batched Request):")
    print("-" * 80)
    for dp_size in test_cases:
        elapsed, num_requests = simulate_new_warmup(dp_size)
        print(f"DP Size: {dp_size:3d} | Time: {elapsed*1000:7.3f} ms | Requests: {num_requests:3d}")

    print("\nSpeedup Analysis:")
    print("-" * 80)
    for dp_size in test_cases:
        old_time, _ = simulate_old_warmup(dp_size)
        new_time, _ = simulate_new_warmup(dp_size)
        speedup = old_time / new_time if new_time > 0 else 0
        saved_time = (old_time - new_time) * 1000
        print(f"DP Size: {dp_size:3d} | Speedup: {speedup:5.2f}x | Time Saved: {saved_time:6.2f} ms")

    print("\n" + "=" * 80)
    print("Key Optimization: Batched warmup reduces from O(dp_size) to O(1) requests")
    print("=" * 80)

if __name__ == "__main__":
    main()

