    rms_norm_impl<scalar_t>(
        output.data_ptr<scalar_t>(),
        input.data_ptr<scalar_t>(),
        weight.data_ptr<scalar_t>(),
        num_tokens,
        hidden_size,
        static_cast<scalar_t>(epsilon));
  });
}

void layer_norm_cpu(
