+      for (; d + kVecSize <= dim; d += kVecSize) {
+        bVec x = bVec::loadu(input_ptr + d);
+        bVec y = bVec::loadu(input_other_ptr + d);
+        bVec act = vf(x);
+        (act * y).store(output_ptr + d);
+      }
