   121	__inline__ __device__ void multi_gpu_barrier(
   122	    uint32_t** signals,
   123	    uint32_t const flag,
   124	    size_t const local_rank,
   125	    size_t const world_size,
   126	    int const tidx,
   127	    int const bidx) {
   128	  // After this function, at least one block in each GPU has reached the barrier
   129	  if (tidx < world_size) {
   130	    // we can think of signals having the shape [world_size, world_size]
   131	    // Dimension 0 is the "listening" dimension, dimension 1 is "emitting" dimension
   132	
   133	    // Block 0 broadcasts its flag (local_rank on emitting dimension) to all receivers
   134	    size_t offset = (flag % 2) ? world_size : 0;
   135	
   136	    if (bidx == 0) {
   137	      st_flag_release(flag, signals[tidx] + offset + local_rank);
   138	    }
   139	
   140	    // All blocks check that corresponding block 0 on other GPUs have set the flag
   141	    // No deadlock because block #0 is always the first block started
   142	    uint32_t* peer_barrier_d = signals[local_rank] + offset + tidx;
   143	    while (ld_flag_acquire(peer_barrier_d) != flag) {
   144	    }
   145	  }
   146	
   147	  __syncthreads();
   148	}
