import time
import math
import random

# Lightweight CPU-bound micro-benchmark to provide a consistent timing harness
# This avoids heavyweight GPU/model dependencies while still exercising Python/NumPy loops.

def bench(name, fn, *args, repeats=5, **kwargs):
    times = []
    for _ in range(repeats):
        t0 = time.perf_counter()
        fn(*args, **kwargs)
        times.append(time.perf_counter() - t0)
    avg = sum(times) / len(times)
    print(f"{name}: avg={avg:.6f}s min={min(times):.6f}s max={max(times):.6f}s over {repeats} runs")


def py_loops(n, iters):
    s = 0
    for _ in range(iters):
        # simple arithmetic to prevent optimization
        for i in range(n):
            s += (i ^ (i >> 1)) & 0xFFFF
    return s


def math_ops(n, iters):
    x = 0.0
    for _ in range(iters):
        for i in range(1, n):
            x += math.sin(i) * math.cos(i) / (i)
    return x


def rand_ops(n, iters):
    s = 0
    rng = random.Random(0)
    for _ in range(iters):
        for _ in range(n):
            s += rng.randint(0, 1000)
    return s


def main():
    # sizes chosen to run quickly but measurably
    bench("py_loops", py_loops, 3000, 5)
    bench("math_ops", math_ops, 1500, 4)
    bench("rand_ops", rand_ops, 3000, 3)


if __name__ == "__main__":
    main()

