import time as _time

# Import the server module
from sglang.srt.entrypoints import http_server as hs

# Monkeypatch time.sleep to speed up the readiness loop
hs.time.sleep = lambda s: None

# Dummy response object for requests
class DummyRes:
    def __init__(self, code=200, data=None):
        self.status_code = code
        self._data = data or {}
        self.text = "OK"
    def json(self):
        return self._data

# Monkeypatch requests.get and requests.post to avoid real network calls
orig_get = hs.requests.get
orig_post = hs.requests.post

def fake_get(url, timeout=5, headers=None):
    # Return model info success immediately
    if url.endswith("/get_model_info"):
        return DummyRes(200, {"is_generation": True})
    return DummyRes(404, {})

def fake_post(url, json=None, headers=None, timeout=None):
    # Simulate successful warmup request
    return DummyRes(200, {"ok": True})

hs.requests.get = fake_get
hs.requests.post = fake_post

class DummyArgs:
    def __init__(self, dp_size=1, skip_tokenizer_init=False):
        self.api_key = None
        self.dp_size = dp_size
        self.skip_tokenizer_init = skip_tokenizer_init
        self.debug_tensor_dump_input_file = None
        self.delete_ckpt_after_loading = False
        self.host = "127.0.0.1"
        self.port = 30000
    def url(self):
        return f"http://{self.host}:{self.port}"


def bench_case(dp, skip_tokenizer_init):
    args = DummyArgs(dp_size=dp, skip_tokenizer_init=skip_tokenizer_init)
    t0 = _time.perf_counter()
    hs._wait_and_warmup(args, None, image_token_text="<img>")
    t1 = _time.perf_counter()
    return t1 - t0


def main():
    for dp in [1, 8, 64, 256]:
        t_text = bench_case(dp, skip_tokenizer_init=False)
        t_ids = bench_case(dp, skip_tokenizer_init=True)
        print({
            "dp": dp,
            "text": round(t_text, 6),
            "input_ids": round(t_ids, 6),
        })

if __name__ == "__main__":
    main()

