import time
import json as _json

try:
    import orjson
except Exception:
    class _OJ:
        def dumps(self, obj):
            return _json.dumps(obj).encode()
    orjson = _OJ()

POST_COUNT = 0

def fake_post(url, json=None, data=None, headers=None, timeout=None):
    global POST_COUNT
    POST_COUNT += 1
    # Simulate small CPU overhead
    s = 0
    for i in range(2000):
        s += i
    return 200


def baseline(dp_size, skip_tokenizer_init=False):
    global POST_COUNT
    POST_COUNT = 0
    json_data = {"sampling_params": {"temperature": 0, "max_new_tokens": 8}}
    if skip_tokenizer_init:
        json_data["input_ids"] = [10, 11, 12]
    else:
        json_data["text"] = "The capital city of France is"

    t0 = time.perf_counter()
    for _ in range(dp_size):
        fake_post("/generate", json=json_data, headers={})
    t1 = time.perf_counter()
    return t1 - t0, POST_COUNT


def optimized(dp_size, skip_tokenizer_init=False):
    global POST_COUNT
    POST_COUNT = 0
    json_data = {"sampling_params": {"temperature": 0, "max_new_tokens": 8}}
    if skip_tokenizer_init:
        if dp_size > 1:
            json_data["input_ids"] = [[10, 11, 12] for _ in range(dp_size)]
        else:
            json_data["input_ids"] = [10, 11, 12]
    else:
        if dp_size > 1:
            json_data["text"] = ["The capital city of France is"] * dp_size
        else:
            json_data["text"] = "The capital city of France is"

    # Use orjson serialization like the optimized server path
    payload = orjson.dumps(json_data)
    t0 = time.perf_counter()
    fake_post("/generate", data=payload, headers={"Content-Type": "application/json"})
    t1 = time.perf_counter()
    return t1 - t0, POST_COUNT


if __name__ == "__main__":
    for dp in (1, 8, 64, 256):
        tb, nb = baseline(dp, skip_tokenizer_init=False)
        to, no = optimized(dp, skip_tokenizer_init=False)
        print({
            "dp": dp,
            "baseline_s": round(tb, 6),
            "baseline_posts": nb,
            "optimized_s": round(to, 6),
            "optimized_posts": no,
        })

