    24	#define WARP_SIZE 32
    25	
    26	template <typename scalar_t>
    27	__global__ void count_and_sort_expert_tokens_kernel(
    28	    const scalar_t* __restrict__ topk_ids,
    29	    int32_t* __restrict__ sorted_token_ids,
    30	    int32_t* __restrict__ cumsum_buffer,
    31	    size_t numel) {
    32	  const size_t tid = blockIdx.x * blockDim.x + threadIdx.x;
    33	  const size_t stride = blockDim.x * gridDim.x;
    34	
    35	  for (size_t i = tid; i < numel; i += stride) {
    36	    int32_t expert_id = topk_ids[i];
    37	    int32_t rank_post_pad = atomicAdd(&cumsum_buffer[expert_id], 1);
    38	    sorted_token_ids[rank_post_pad] = i;
    39	  }
    40	}
