    42	template <typename scalar_t>
    43	__global__ void moe_align_block_size_kernel(
    44	    const scalar_t* __restrict__ topk_ids,
    45	    int32_t* __restrict__ sorted_token_ids,
    46	    int32_t* __restrict__ expert_ids,
    47	    int32_t* __restrict__ total_tokens_post_pad,
    48	    int32_t num_experts,
    49	    int32_t padded_num_experts,
    50	    int32_t experts_per_warp,
    51	    int32_t block_size,
    52	    size_t numel,
    53	    int32_t* __restrict__ cumsum) {
    54	  extern __shared__ int32_t shared_counts[];
    55	
    56	  const int warp_id = threadIdx.x / WARP_SIZE;
    57	  const int my_expert_start = warp_id * experts_per_warp;
    58	
    59	  for (int i = 0; i < experts_per_warp; ++i) {
    60	    if (my_expert_start + i < padded_num_experts) {
    61	      shared_counts[warp_id * experts_per_warp + i] = 0;
    62	    }
    63	  }
    64	
    65	  __syncthreads();
    66	
    67	  const size_t tokens_per_thread = CEILDIV(numel, blockDim.x);
    68	  const size_t start_idx = threadIdx.x * tokens_per_thread;
    69	
    70	  for (int i = start_idx; i < numel && i < start_idx + tokens_per_thread; ++i) {
    71	    int expert_id = topk_ids[i];
    72	    int warp_idx = expert_id / experts_per_warp;
    73	    int expert_offset = expert_id % experts_per_warp;
    74	    atomicAdd(&shared_counts[warp_idx * experts_per_warp + expert_offset], 1);
    75	  }
    76	
    77	  __syncthreads();
    78	
    79	  if (threadIdx.x == 0) {
    80	    cumsum[0] = 0;
    81	    for (int i = 1; i <= num_experts; ++i) {
    82	      int expert_count = 0;
    83	      int warp_idx = (i - 1) / experts_per_warp;
    84	      int expert_offset = (i - 1) % experts_per_warp;
    85	      expert_count = shared_counts[warp_idx * experts_per_warp + expert_offset];
    86	
    87	      cumsum[i] = cumsum[i - 1] + CEILDIV(expert_count, block_size) * block_size;
    88	    }
    89	    *total_tokens_post_pad = cumsum[num_experts];
    90	  }
    91	
    92	  __syncthreads();
    93	
    94	  if (threadIdx.x < num_experts) {
    95	    for (int i = cumsum[threadIdx.x]; i < cumsum[threadIdx.x + 1]; i += block_size) {
    96	      expert_ids[i / block_size] = threadIdx.x;
    97	    }
    98	  }
    99	}
