   101	void moe_align_block_size(
   102	    torch::Tensor topk_ids,
   103	    int64_t num_experts,
   104	    int64_t block_size,
   105	    torch::Tensor sorted_token_ids,
   106	    torch::Tensor experts_ids,
   107	    torch::Tensor num_tokens_post_pad,
   108	    torch::Tensor token_cnts_buffer,
   109	    torch::Tensor cumsum_buffer) {
   110	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
   111	
   112	  int64_t padded_num_experts = ((num_experts + WARP_SIZE - 1) / WARP_SIZE) * WARP_SIZE;
   113	
   114	  int experts_per_warp;
   115	  int threads;
   116	
   117	  if (num_experts <= 8) {
   118	    experts_per_warp = 8;
   119	    threads = 256;
   120	  } else if (num_experts <= 16) {
   121	    experts_per_warp = 16;
   122	    threads = 512;
   123	  } else {
   124	    experts_per_warp = WARP_SIZE;
   125	    threads = 1024;
   126	  }
   127	
   128	  threads = ((threads + WARP_SIZE - 1) / WARP_SIZE) * WARP_SIZE;
   129	
   130	  DISPATCH_INTEGRAL_TYPES(topk_ids.scalar_type(), "moe_align_block_size_kernel", [&] {
   131	    auto align_kernel = moe_align_block_size_kernel<scalar_t>;
   132	
   133	    size_t num_warps = CEILDIV(padded_num_experts, experts_per_warp);
   134	    size_t shared_mem_size = num_warps * experts_per_warp * sizeof(int32_t);
   135	
   136	    align_kernel<<<1, threads, shared_mem_size, stream>>>(
   137	        topk_ids.data_ptr<scalar_t>(),
   138	        sorted_token_ids.data_ptr<int32_t>(),
   139	        experts_ids.data_ptr<int32_t>(),
   140	        num_tokens_post_pad.data_ptr<int32_t>(),
   141	        num_experts,
   142	        padded_num_experts,
   143	        experts_per_warp,
   144	        block_size,
   145	        topk_ids.numel(),
   146	        cumsum_buffer.data_ptr<int32_t>());
   147	
   148	    const int block_threads = std::min(256, (int)threads);
   149	    const int num_blocks = (topk_ids.numel() + block_threads - 1) / block_threads;
   150	    const int max_blocks = 65535;
   151	    const int actual_blocks = std::min(num_blocks, max_blocks);
   152	
   153	    auto sort_kernel = count_and_sort_expert_tokens_kernel<scalar_t>;
   154	    sort_kernel<<<actual_blocks, block_threads, 0, stream>>>(
   155	        topk_ids.data_ptr<scalar_t>(),
   156	        sorted_token_ids.data_ptr<int32_t>(),
   157	        cumsum_buffer.data_ptr<int32_t>(),
   158	        topk_ids.numel());
   159	  });
   160	}
