import time
import random

# Micro-benchmark for PrefillAdder.remove_running_tokens and queue filtering patterns.
from sglang.srt.managers.policy_scheduler import PrefillAdder, CLIP_MAX_NEW_TOKENS

class DummySampling:
    def __init__(self, max_new_tokens):
        self.max_new_tokens = max_new_tokens

class DummyReq:
    def __init__(self, max_new_tokens, out_len):
        self.sampling_params = DummySampling(max_new_tokens)
        self.output_ids = [0] * out_len

class DummyBatch:
    def __init__(self, reqs):
        self.reqs = reqs

class DummyTree:
    def inc_lock_ref(self, node):
        return 0
    def dec_lock_ref(self, node):
        return 0


def bench_remove_running_tokens(n=20000):
    random.seed(0)
    reqs = [
        DummyReq(max_new_tokens=random.randint(1, 4096), out_len=random.randint(0, 1024))
        for _ in range(n)
    ]
    batch = DummyBatch(reqs)
    adder = PrefillAdder(DummyTree(), rem_total_tokens=10**12, rem_input_tokens=10**12, rem_chunk_tokens=None)
    t0 = time.time()
    adder.remove_running_tokens(batch, new_token_ratio=0.5)
    t1 = time.time()
    return t1 - t0


def bench_queue_filter(nw=30000, nr=10000):
    # Simulate waiting queue filtering: remove can_run_list from waiting_queue
    waiting_queue = list(range(nw))
    can_run_list = set(random.sample(waiting_queue, nr))

    # Baseline: list membership (slow)
    t0 = time.time()
    filtered1 = [x for x in waiting_queue if x not in list(can_run_list)]
    t1 = time.time()

    # Optimized: set membership (fast)
    t2 = time.time()
    can_run_set = can_run_list
    filtered2 = [x for x in waiting_queue if x not in can_run_set]
    t3 = time.time()

    # Return times to compare
    return (t1 - t0), (t3 - t2), (len(filtered1), len(filtered2))


if __name__ == "__main__":
    t_remove = bench_remove_running_tokens()
    t_list, t_set, sizes = bench_queue_filter()
    print({
        "remove_running_tokens_s": round(t_remove, 6),
        "queue_filter_list_s": round(t_list, 6),
        "queue_filter_set_s": round(t_set, 6),
        "sizes": sizes,
        "clip": CLIP_MAX_NEW_TOKENS,
    })

