import time
import random

# Micro-benchmarks of patterns we will optimize in the repository:
# 1) Summation over requests using list vs generator
# 2) Filtering large lists with list vs set membership

CLIP_MAX_NEW_TOKENS = 4096

class DummySampling:
    def __init__(self, max_new_tokens):
        self.max_new_tokens = max_new_tokens

class DummyReq:
    def __init__(self, max_new_tokens, out_len):
        self.sampling_params = DummySampling(max_new_tokens)
        self.output_ids = [0] * out_len

class DummyBatch:
    def __init__(self, reqs):
        self.reqs = reqs


def remove_running_tokens_list(batch, new_token_ratio: float):
    # Baseline: materialize list inside sum
    return sum([
        min((r.sampling_params.max_new_tokens - len(r.output_ids)), CLIP_MAX_NEW_TOKENS) * new_token_ratio
        for r in batch.reqs
    ])


def remove_running_tokens_gen(batch, new_token_ratio: float):
    # Optimized: generator to avoid intermediate list allocation
    return sum(
        min((r.sampling_params.max_new_tokens - len(r.output_ids)), CLIP_MAX_NEW_TOKENS) * new_token_ratio
        for r in batch.reqs
    )


def bench_remove_running_tokens(n=20000):
    random.seed(0)
    reqs = [
        DummyReq(max_new_tokens=random.randint(1, 4096), out_len=random.randint(0, 1024))
        for _ in range(n)
    ]
    batch = DummyBatch(reqs)

    t0 = time.time();
    x1 = remove_running_tokens_list(batch, 0.5)
    t1 = time.time();

    t2 = time.time();
    x2 = remove_running_tokens_gen(batch, 0.5)
    t3 = time.time();

    assert abs(x1 - x2) < 1e-6
    return t1 - t0, t3 - t2


def bench_queue_filter(nw=30000, nr=10000):
    # Simulate waiting queue filtering: remove can_run_list from waiting_queue
    random.seed(1)
    waiting_queue = list(range(nw))
    can_run_list = random.sample(waiting_queue, nr)

    # Baseline: list membership (slow)
    t0 = time.time()
    filtered1 = [x for x in waiting_queue if x not in can_run_list]
    t1 = time.time()

    # Optimized: set membership (fast)
    can_run_set = set(can_run_list)
    t2 = time.time()
    filtered2 = [x for x in waiting_queue if x not in can_run_set]
    t3 = time.time()

    assert filtered1 == filtered2
    return (t1 - t0), (t3 - t2), (len(filtered1), len(filtered2))


if __name__ == "__main__":
    t_list_sum, t_gen_sum = bench_remove_running_tokens()
    t_list, t_set, sizes = bench_queue_filter()
    print({
        "remove_sum_list_s": round(t_list_sum, 6),
        "remove_sum_gen_s": round(t_gen_sum, 6),
        "queue_filter_list_s": round(t_list, 6),
        "queue_filter_set_s": round(t_set, 6),
        "sizes": sizes,
    })

