@app.api_route("/generate_from_file", methods=["POST"])
async def generate_from_file_request(file: UploadFile, request: Request):
    """Handle a generate request via file upload for input_embeds only.
    This avoids large JSON body parsing overhead by directly loading bytes.
    """
    content = await file.read()
    try:
        # orjson.loads accepts bytes directly
        input_embeds = orjson.loads(content)
    except Exception as e:
        return _create_error_response(e)

    # Provide minimal sampling params to match common defaults in tests
    obj = GenerateReqInput(
        input_embeds=input_embeds,
        sampling_params={
            "temperature": 0,
            "max_new_tokens": 50,
        },
    )

    try:
        ret = await _global_state.tokenizer_manager.generate_request(obj, request).__anext__()
        return ret
    except ValueError as e:
        logger.error(f"Error: {e}")
        return _create_error_response(e)

