import time
import timeit


def timed(msg, fn, repeat=5):
    times = timeit.repeat(fn, number=1, repeat=repeat)
    print(f"{msg}: min={min(times):.6f}s avg={sum(times)/len(times):.6f}s over {repeat} runs")


def bench_torch_allocations():
    try:
        import torch
    except Exception as e:
        print("torch not available, skipping torch benchmarks.")
        return

    # CPU allocations to avoid GPU dependency
    def alloc_zeros():
        x = torch.zeros((1024, 1024))
        return x

    def alloc_empty():
        x = torch.empty((1024, 1024))
        return x

    # Repeat to amplify differences
    def many_alloc_zeros():
        for _ in range(64):
            alloc_zeros()

    def many_alloc_empty():
        for _ in range(64):
            alloc_empty()

    print("Benchmark: torch tensor allocations (CPU)")
    timed("zeros", many_alloc_zeros)
    timed("empty", many_alloc_empty)


if __name__ == "__main__":
    print("Simple allocation benchmark proxy for repository optimization")
    bench_torch_allocations()

