        size=(num_gpus, num_layers, num_logical_experts),
        fill_value=-1,
        dtype=dtype,
    )

    for layer_id in range(num_layers):
        for logical_expert_id in range(num_logical_experts):
            candidates = [
                x
                for x in logical_to_all_physical_map[layer_id, logical_expert_id].tolist()
                if x != -1
            ]
            out_part = full[:, layer_id, logical_expert_id]
            for gpu_id in range(num_gpus):
                same_gpu = [
                    pid for pid in candidates if (pid // num_local_physical_experts) == gpu_id
