        # get configs and target modules
        self.configs = {}
        self.origin_target_modules = set()
        for name, path in self.lora_paths.items():
            conf = LoRAConfig(path)
            self.configs[name] = conf
            self.origin_target_modules |= set(conf.target_modules)
