   153	__global__ void reshape_and_cache_kernel(
   154	    const scalar_t* __restrict__ key,    // [num_tokens, num_heads, head_size]
   155	    const scalar_t* __restrict__ value,  // [num_tokens, num_heads, head_size]
   156	    cache_t* __restrict__ key_cache,     // [num_blocks, num_heads, head_size/x,
   157	                                         // block_size, x]
   158	    cache_t* __restrict__ value_cache,   // [num_blocks, num_heads, head_size,
   159	                                         // block_size]
   160	    const int64_t* __restrict__ slot_mapping,  // [num_tokens]
   161	    const int key_stride, const int value_stride, const int num_heads,
   162	    const int head_size, const int block_size, const int x, const float k_scale,
   163	    const float v_scale) {
   164	  const int64_t token_idx = blockIdx.x;
   165	  const int64_t slot_idx = slot_mapping[token_idx];
   166	  if (slot_idx < 0) {
   167	    // Padding token that should be ignored.
   168	    return;
   169	  }
   170	
   171	  const int64_t block_idx = slot_idx / block_size;
   172	  const int64_t block_offset = slot_idx % block_size;
   173	
   174	  const int n = num_heads * head_size;
   175	  for (int i = threadIdx.x; i < n; i += blockDim.x) {
   176	    const int64_t src_key_idx = token_idx * key_stride + i;
   177	    const int64_t src_value_idx = token_idx * value_stride + i;
   178	
   179	    const int head_idx = i / head_size;
   180	    const int head_offset = i % head_size;
   181	    const int x_idx = head_offset / x;
   182	    const int x_offset = head_offset % x;
   183	
   184	    const int64_t tgt_key_idx =
   185	        block_idx * num_heads * (head_size / x) * block_size * x +
   186	        head_idx * (head_size / x) * block_size * x + x_idx * block_size * x +
   187	        block_offset * x + x_offset;
   188	    const int64_t tgt_value_idx =
   189	        block_idx * num_heads * head_size * block_size +
   190	        head_idx * head_size * block_size + head_offset * block_size +
   191	        block_offset;
   192	    scalar_t tgt_key = key[src_key_idx];
   193	    scalar_t tgt_value = value[src_value_idx];
   194	    if constexpr (kv_dt == Fp8KVCacheDataType::kAuto) {
   195	      key_cache[tgt_key_idx] = tgt_key;
   196	      value_cache[tgt_value_idx] = tgt_value;
   197	    } else {
   198	      key_cache[tgt_key_idx] =
   199	          fp8::scaled_convert<cache_t, scalar_t, kv_dt>(tgt_key, k_scale);
   200	      value_cache[tgt_value_idx] =
   201	          fp8::scaled_convert<cache_t, scalar_t, kv_dt>(tgt_value, v_scale);
   202	    }
   203	  }
   204	}
