    24	void swap_blocks(torch::Tensor& src, torch::Tensor& dst,
    25	                 const torch::Tensor& block_mapping) {
    26	  torch::Device src_device = src.device();
    27	  torch::Device dst_device = dst.device();
    28	  cudaMemcpyKind memcpy_type;
    29	  if (src_device.is_cuda() && dst_device.is_cuda()) {
    30	    TORCH_CHECK(src_device.index() == dst_device.index(),
    31	                "src and dst must be on the same GPU");
    32	    memcpy_type = cudaMemcpyDeviceToDevice;
    33	  } else if (src_device.is_cuda() && dst_device.is_cpu()) {
    34	    memcpy_type = cudaMemcpyDeviceToHost;
    35	  } else if (src_device.is_cpu() && dst_device.is_cuda()) {
    36	    memcpy_type = cudaMemcpyHostToDevice;
    37	  } else {
    38	    TORCH_CHECK(false, "Invalid device combination");
    39	  }
