   172	                        torch::Tensor& residual,  // [..., hidden_size]
   173	                        torch::Tensor& weight,    // [hidden_size]
   174	                        double epsilon) {
   175	  int hidden_size = input.size(-1);
   176	  int num_tokens = input.numel() / hidden_size;
