   178	  dim3 grid(num_tokens);
   179	  /* This kernel is memory-latency bound in many scenarios.
   180	     When num_tokens is large, a smaller block size allows
   181	     for increased block occupancy on CUs and better latency
   182	     hiding on global mem ops. */
   183	  const int max_block_size = (num_tokens < 256) ? 1024 : 256;
   184	  dim3 block(std::min(hidden_size, max_block_size));
   185	  const at::cuda::OptionalCUDAGuard device_guard(device_of(input));
   186	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
   187	  /*If the tensor types are FP16/BF16, try to use the optimized kernel
   188	    with packed + vectorized ops.
   189	    Max optimization is achieved with a width-8 vector of FP16/BF16s
   190	    since we can load at most 128 bits at once in a global memory op.
   191	    However, this requires each tensor's data to be aligned to 16
   192	    bytes.
   193	   */
   194	  auto inp_ptr = reinterpret_cast<std::uintptr_t>(input.data_ptr());
   195	  auto res_ptr = reinterpret_cast<std::uintptr_t>(residual.data_ptr());
   196	  auto wt_ptr = reinterpret_cast<std::uintptr_t>(weight.data_ptr());
   197	  bool ptrs_are_aligned =
   198	      inp_ptr % 16 == 0 && res_ptr % 16 == 0 && wt_ptr % 16 == 0;
   199	  if (ptrs_are_aligned && hidden_size % 8 == 0) {
   200	    LAUNCH_FUSED_ADD_RMS_NORM(8);
   201	  } else {
   202	    LAUNCH_FUSED_ADD_RMS_NORM(0);
