import time
import torch

def benchmark():
    start = time.time()
    # Example operation; replace with actual usage
    x = torch.empty((1000, 1000))
    for _ in range(1000):
        x += torch.rand((1000, 1000))
    end = time.time()
    print(f"Elapsed time: {end - start} seconds")

if __name__ == "__main__":
    benchmark()

