    17	template <typename scalar_t>
    18	__global__ void moe_align_block_size_kernel(
    19	    const scalar_t* __restrict__ topk_ids,
    20	    int32_t* __restrict__ sorted_token_ids, int32_t* __restrict__ expert_ids,
    21	    int32_t* __restrict__ total_tokens_post_pad, int32_t num_experts,
    22	    int32_t padded_num_experts, int32_t experts_per_warp, int32_t block_size,
    23	    size_t numel, int32_t* __restrict__ cumsum, int32_t max_num_tokens_padded) {
    24	  extern __shared__ int32_t shared_counts[];
    25	
    26	  const int warp_id = threadIdx.x / WARP_SIZE;
    27	  const int my_expert_start = warp_id * experts_per_warp;
    28	
    29	  for (int i = 0; i < experts_per_warp; ++i) {
    30	    if (my_expert_start + i < padded_num_experts) {
    31	      shared_counts[warp_id * experts_per_warp + i] = 0;
    32	    }
    33	  }
    34	
    35	  __syncthreads();
    36	
    37	  const size_t tid = threadIdx.x;
    38	  const size_t stride = blockDim.x;
    39	
    40	  for (size_t i = tid; i < numel; i += stride) {
    41	    int expert_id = topk_ids[i];
    42	    int warp_idx = expert_id / experts_per_warp;
    43	    int expert_offset = expert_id % experts_per_warp;
    44	    atomicAdd(&shared_counts[warp_idx * experts_per_warp + expert_offset], 1);
    45	  }
    46	
    47	  __syncthreads();
    48	
    49	  // Compute prefix sum over token counts per expert
    50	  using BlockScan = cub::BlockScan<int32_t, 1024>;
    51	  __shared__ typename BlockScan::TempStorage temp_storage;
    52	
    53	  int expert_count = 0;
    54	  int expert_id = threadIdx.x;
    55	  if (expert_id < num_experts) {
    56	    int warp_idx = expert_id / experts_per_warp;
    57	    int expert_offset = expert_id % experts_per_warp;
    58	    expert_count = shared_counts[warp_idx * experts_per_warp + expert_offset];
    59	    expert_count = CEILDIV(expert_count, block_size) * block_size;
    60	  }
    61	
    62	  int cumsum_val;
    63	  BlockScan(temp_storage).ExclusiveSum(expert_count, cumsum_val);
    64	  if (expert_id <= num_experts) {
    65	    cumsum[expert_id] = cumsum_val;
    66	  }
    67	
    68	  if (expert_id == num_experts) {
    69	    *total_tokens_post_pad = cumsum_val;
    70	  }
    71	
    72	  __syncthreads();
    73	
    74	  if (threadIdx.x < num_experts) {
    75	    for (int i = cumsum[threadIdx.x]; i < cumsum[threadIdx.x + 1];
    76	         i += block_size) {
    77	      expert_ids[i / block_size] = threadIdx.x;
    78	    }
    79	  }
    80	
    81	  // Only fill the padded regions of sorted_token_ids with sentinel `numel`.
    82	  if (threadIdx.x < num_experts) {
    83	    int warp_idx = threadIdx.x / experts_per_warp;
    84	    int expert_offset = threadIdx.x % experts_per_warp;
    85	    int actual_cnt = shared_counts[warp_idx * experts_per_warp + expert_offset];
    86	    int start = cumsum[threadIdx.x] + actual_cnt;
    87	    int end = cumsum[threadIdx.x + 1];
    88	    for (int i = start; i < end; ++i) {
    89	      sorted_token_ids[i] = static_cast<int32_t>(numel);
    90	    }
    91	  }
    92	}
