   109	template <typename scalar_t, int TOPK>
   110	__global__ void moe_sum_kernel(
   111	    scalar_t* __restrict__ out,          // [..., d]
   112	    const scalar_t* __restrict__ input,  // [..., topk, d]
   113	    const int d) {
   114	  const int64_t token_idx = blockIdx.x;
   115	  for (int64_t idx = threadIdx.x; idx < d; idx += blockDim.x) {
   116	    scalar_t x = 0.0;
   117	#pragma unroll
   118	    for (int k = 0; k < TOPK; ++k) {
   119	      x += VLLM_LDG(&input[token_idx * TOPK * d + k * d + idx]);
   120	    }
   121	    out[token_idx * d + idx] = x;
   122	  }
   123	}
