   125	template <typename scalar_t>
   126	__global__ void moe_align_block_size_small_batch_expert_kernel(
   127	    const scalar_t* __restrict__ topk_ids,
   128	    int32_t* __restrict__ sorted_token_ids, int32_t* __restrict__ expert_ids,
   129	    int32_t* __restrict__ total_tokens_post_pad, int32_t num_experts,
   130	    int32_t block_size, size_t numel, int32_t max_num_tokens_padded) {
   131	  const size_t tid = threadIdx.x;
   132	  const size_t stride = blockDim.x;
   133	
   134	  extern __shared__ int32_t shared_mem[];
   135	  int32_t* cumsum = shared_mem;
   136	  int32_t* tokens_cnts = (int32_t*)(shared_mem + num_experts + 1);
   137	
   138	  // Zero per-thread token counts (rows 1..blockDim.x)
   139	  for (int idx = threadIdx.x; idx < (int)(blockDim.x * num_experts); idx += blockDim.x) {
   140	    tokens_cnts[num_experts + idx] = 0;
   141	  }
   142	
   143	  for (size_t i = tid; i < numel; i += stride) {
   144	    ++tokens_cnts[(threadIdx.x + 1) * num_experts + topk_ids[i]];
   145	  }
   146	
   147	  __syncthreads();
   148	
   149	  if (threadIdx.x < num_experts) {
   150	    tokens_cnts[threadIdx.x] = 0;
   151	    for (int i = 1; i <= blockDim.x; ++i) {
   152	      tokens_cnts[i * num_experts + threadIdx.x] +=
   153	          tokens_cnts[(i - 1) * num_experts + threadIdx.x];
   154	    }
   155	  }
   156	
   157	  __syncthreads();
   158	
   159	  if (threadIdx.x == 0) {
   160	    cumsum[0] = 0;
   161	    for (int i = 1; i <= num_experts; ++i) {
   162	      cumsum[i] =
   163	          cumsum[i - 1] +
   164	          CEILDIV(tokens_cnts[blockDim.x * num_experts + i - 1], block_size) *
   165	              block_size;
   166	    }
   167	    *total_tokens_post_pad = static_cast<int32_t>(cumsum[num_experts]);
   168	  }
   169	
   170	  __syncthreads();
   171	
   172	  if (threadIdx.x < num_experts) {
   173	    for (int i = cumsum[threadIdx.x]; i < cumsum[threadIdx.x + 1];
   174	         i += block_size) {
   175	      expert_ids[i / block_size] = threadIdx.x;
   176	    }
   177	  }
   178	
   179	  // Only fill the padded regions of sorted_token_ids with sentinel `numel`.
   180	  if (threadIdx.x < num_experts) {
   181	    int actual_cnt = tokens_cnts[blockDim.x * num_experts + threadIdx.x];
   182	    int start = cumsum[threadIdx.x] + actual_cnt;
   183	    int end = cumsum[threadIdx.x + 1];
   184	    for (int i = start; i < end; ++i) {
   185	      sorted_token_ids[i] = static_cast<int32_t>(numel);
   186	    }
   187	  }
   188	
   189	  for (size_t i = tid; i < numel; i += stride) {
   190	    int32_t expert_id = topk_ids[i];
   191	    int32_t rank_post_pad =
   192	        tokens_cnts[threadIdx.x * num_experts + expert_id] + cumsum[expert_id];
   193	    sorted_token_ids[rank_post_pad] = i;
   194	    ++tokens_cnts[threadIdx.x * num_experts + expert_id];
   195	  }
   196	}
