   201	// taken from
   202	// https://github.com/sgl-project/sglang/blob/8b5f83ed3b7d2a49ad5c5cd5aa61c5d502f47dbc
   203	void moe_align_block_size(torch::Tensor topk_ids, int64_t num_experts,
   204	                          int64_t block_size, torch::Tensor sorted_token_ids,
   205	                          torch::Tensor experts_ids,
   206	                          torch::Tensor num_tokens_post_pad) {
   207	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
   208	
   209	  int64_t padded_num_experts =
   210	      ((num_experts + WARP_SIZE - 1) / WARP_SIZE) * WARP_SIZE;
   211	  int experts_per_warp = WARP_SIZE;
   212	  int threads = 1024;
   213	  threads = ((threads + WARP_SIZE - 1) / WARP_SIZE) * WARP_SIZE;
   214	
   215	  // BlockScan uses 1024 threads and assigns one thread per expert.
   216	  TORCH_CHECK(padded_num_experts < 1024,
   217	              "padded_num_experts must be less than 1024");
   218	
   219	  VLLM_DISPATCH_INTEGRAL_AND_UNSIGNED_TYPES(
   220	      topk_ids.scalar_type(), "moe_align_block_size_kernel", [&] {
   221	        // calc needed amount of shared mem for `cumsum` tensors
   222	        auto options_int =
   223	            torch::TensorOptions().dtype(torch::kInt).device(topk_ids.device());
   224	        torch::Tensor cumsum_buffer =
   225	            torch::empty({num_experts + 1}, options_int);
   226	        bool small_batch_expert_mode =
   227	            (topk_ids.numel() < 1024) && (num_experts <= 64);
   228	
   229	        if (small_batch_expert_mode) {
   230	          const int32_t threads = max((int32_t)num_experts, WARP_SIZE);
   231	          const int32_t shared_mem_size =
   232	              ((threads + 1) * num_experts + (num_experts + 1)) *
   233	              sizeof(int32_t);
   234	
   235	          auto small_batch_expert_kernel =
   236	              vllm::moe::moe_align_block_size_small_batch_expert_kernel<
   237	                  scalar_t>;
   238	          small_batch_expert_kernel<<<1, threads, shared_mem_size, stream>>>(
   239	              topk_ids.data_ptr<scalar_t>(),
   240	              sorted_token_ids.data_ptr<int32_t>(),
   241	              experts_ids.data_ptr<int32_t>(),
   242	              num_tokens_post_pad.data_ptr<int32_t>(), num_experts, block_size,
   243	              topk_ids.numel(), sorted_token_ids.size(0));
   244	        } else {
   245	          auto align_kernel = vllm::moe::moe_align_block_size_kernel<scalar_t>;
   246	
   247	          size_t num_warps = CEILDIV(padded_num_experts, experts_per_warp);
   248	          size_t shared_mem_size =
   249	              num_warps * experts_per_warp * sizeof(int32_t);
   250	
   251	          align_kernel<<<1, threads, shared_mem_size, stream>>>(
   252	              topk_ids.data_ptr<scalar_t>(),
   253	              sorted_token_ids.data_ptr<int32_t>(),
   254	              experts_ids.data_ptr<int32_t>(),
   255	              num_tokens_post_pad.data_ptr<int32_t>(), num_experts,
   256	              padded_num_experts, experts_per_warp, block_size,
   257	              topk_ids.numel(), cumsum_buffer.data_ptr<int32_t>(),
   258	              sorted_token_ids.size(0));
   259	
   260	          const int block_threads = std::min(256, (int)threads);
   261	          const int num_blocks =
   262	              (topk_ids.numel() + block_threads - 1) / block_threads;
   263	          const int max_blocks = 65535;
   264	          const int actual_blocks = std::min(num_blocks, max_blocks);
   265	
   266	          auto sort_kernel =
   267	              vllm::moe::count_and_sort_expert_tokens_kernel<scalar_t>;
   268	          sort_kernel<<<actual_blocks, block_threads, 0, stream>>>(
   269	              topk_ids.data_ptr<scalar_t>(),
   270	              sorted_token_ids.data_ptr<int32_t>(),
   271	              cumsum_buffer.data_ptr<int32_t>(), topk_ids.numel());
   272	        }
   273	      });
   274	}
