   276	void moe_sum(torch::Tensor& input,   // [num_tokens, topk, hidden_size]
   277	             torch::Tensor& output)  // [num_tokens, hidden_size]
   278	{
   279	  const int hidden_size = input.size(-1);
   280	  const auto num_tokens = output.numel() / hidden_size;
   281	  const int topk = input.size(1);
   282	
   283	  dim3 grid(num_tokens);
   284	  dim3 block(std::min(hidden_size, 1024));
   285	  const at::cuda::OptionalCUDAGuard device_guard(device_of(output));
   286	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
   287	
   288	  switch (topk) {
   289	    case 2:
   290	      VLLM_DISPATCH_FLOATING_TYPES(input.scalar_type(), "moe_sum_kernel", [&] {
   291	        vllm::moe::moe_sum_kernel<scalar_t, 2><<<grid, block, 0, stream>>>(
   292	            output.data_ptr<scalar_t>(), input.data_ptr<scalar_t>(),
   293	            hidden_size);
   294	      });
   295	      break;
   296	
   297	    case 3:
   298	      VLLM_DISPATCH_FLOATING_TYPES(input.scalar_type(), "moe_sum_kernel", [&] {
   299	        vllm::moe::moe_sum_kernel<scalar_t, 3><<<grid, block, 0, stream>>>(
   300	            output.data_ptr<scalar_t>(), input.data_ptr<scalar_t>(),
   301	            hidden_size);
   302	      });
   303	      break;
   304	
   305	    case 4:
   306	      VLLM_DISPATCH_FLOATING_TYPES(input.scalar_type(), "moe_sum_kernel", [&] {
   307	        vllm::moe::moe_sum_kernel<scalar_t, 4><<<grid, block, 0, stream>>>(
   308	            output.data_ptr<scalar_t>(), input.data_ptr<scalar_t>(),
   309	            hidden_size);
   310	      });
   311	      break;
   312	
   313	    default:
   314	      at::sum_out(output, input, 1);
   315	      break;
   316	  }
   317	}
