def mark_blocks_as_computed(self, seq_group: SequenceGroup):
    # Mark cached blocks as computed right after scheduling to avoid
    # redundant scans in subsequent iterations.
    if not self.enable_caching:
        return

    block_ids_to_mark: List[int] = []
    for seq in seq_group.get_seqs(status=SequenceStatus.RUNNING):
        block_ids = self.block_tables[seq.seq_id].physical_block_ids
        if not block_ids:
            continue
        # Skip the last block to avoid caching full sequences.
        if len(block_ids) > 1:
            block_ids_to_mark.extend(block_ids[:-1])
        else:
            # Single-block prompt: only mark if it is full to be safe.
            blocks = self.block_tables[seq.seq_id].blocks
            if blocks and blocks[-1].is_full:
                block_ids_to_mark.append(block_ids[0])

    if block_ids_to_mark:
        # Deduplicate while preserving order
        seen = set()
        deduped = [bid for bid in block_ids_to_mark if not (bid in seen or seen.add(bid))]
        self.block_allocator.mark_blocks_as_computed(deduped)

